import React from 'react'
import Footer from './Components/Footer';
import Header from './Components/Header';
import Items from './Components/Items';
import Categories from './Components/Categories';
import FullItem from './Components/FullItem';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      orders: [],
      fullItem: false,
      currentItems: [],
      fullItems : [],
      items: [
        {
          id: '1',
          title: 'Стул серый',
          img: 'chair-grey.jpg',
          desc: 'Wonderful product',
          category:'chairs',
          price:'49.99'
        },
        {
          id: '2',
          title: 'Кресло серое',
          img: 'armchair-grey.jpg',
          desc: 'Wonderful product',
          category:'chairs',
          price:'69.99'
        },
        {
          id: '3',
          title: 'Шкаф для кухни',
          img: 'kitchens wardrobe.jpg',
          desc: 'Wonderful product',
          category:'tables',
          price:'59.99'
        },
        {
          id: '4',
          title: 'Диван серый',
          img: 'grey sofa.jpg',
          desc: 'Wonderful product',
          category:'sofas',
          price:'499.99'
        }
      ]
    }
    this.state.currentItems = this.state.items
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
    this.chooseCategory = this.chooseCategory.bind(this)
    this.toShowItem = this.toShowItem.bind(this)
  }
  render() {
  return (
    <div className='wrapper'>
      <Header orders={this.state.orders} toDelete = {this.deleteOrder}/>
      <Categories chooseCategory = {this.chooseCategory}/>
      <Items items = {this.state.currentItems} toAdd = {this.addToOrder} toShow = {this.toShowItem}/>
      {this.state.fullItem && <FullItem toAdd = {this.addToOrder} toShow = {this.toShowItem} item = {this.state.fullItems}/>}
      <Footer/>


    </div>
  )
  }

  toShowItem(item){
    this.setState({fullItems: item})
    this.setState({fullItem: !this.state.fullItem})
  }

  chooseCategory(category){
    if (category === 'all'){
      this.setState({currentItems: this.state.items})
      return
    }

    this.setState({
      currentItems: this.state.items.filter(el => el.category === category)
    })
  }

  deleteOrder(id){
    this.setState({orders: this.state.orders.filter(el => el.id !== id)})
  }
  addToOrder(item){
    let isInArray = false
    this.state.orders.forEach(el => {
      if (el.id === item.id)
      isInArray = true
    })
      if(!isInArray)
    this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
