import React, { Component } from 'react'
import './styles/Fullitem.css';

export class FullItem extends Component {
  render() {
    return (
      <div className='full_item_fullItem'>
        <div className=''>
            <img src={'./img/' + this.props.item.img} alt="" onClick={() => this.props.toShow(this.props.item)} />
            <h2>{this.props.item.title}</h2> 
            <p>{this.props.item.desc}</p>
            <b>{this.props.item.price}$</b>
            <div className='add_item_item' onClick={() => this.props.toAdd(this.props.item)}>+</div>  
        </div>
      </div>
    )
  }
}

export default FullItem
