import React from 'react'
import Footer from './Components/Footer';
import Header from './Components/Header';
import Items from './Components/Items';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      items: [
        {
          id: '1',
          title: 'Стул серый',
          img: 'chair-grey.jpeg',
          desc: 'Wonderful product',
          category:'chairs',
          price:'49.99'
        },
        {
          id: '2',
          title: 'Кресло серое',
          img: 'chair-grey.jpeg',
          desc: 'Wonderful product',
          category:'chairs',
          price:'69.99'
        },
        {
          id: '3',
          title: 'Шкаф для кухни',
          img: 'chair-grey.jpeg',
          desc: 'Wonderful product',
          category:'tables',
          price:'59.99'
        },
        {
          id: '4',
          title: 'Швейный стол',
          img: 'chair-grey.jpeg',
          desc: 'Wonderful product',
          category:'tables',
          price:'499.99'
        }
      ]
    }
  }
  render() {
  return (
    <div className='wrapper'>
      <Header/>
      <Items items = {this.state.items}/>
      <Footer/>
    </div>
  )
  }
}

export default App;
