import React from 'react'
import Footer from './Components/Footer';
import Header from './Components/Header';
import Items from './Components/Items';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      orders: [],
      items: [
        {
          id: '1',
          title: 'Стул серый',
          img: 'chair-grey.jpg',
          desc: 'Wonderful product',
          category:'chairs',
          price:'49.99'
        },
        {
          id: '2',
          title: 'Кресло серое',
          img: 'armchair-grey.jpg',
          desc: 'Wonderful product',
          category:'chairs',
          price:'69.99'
        },
        {
          id: '3',
          title: 'Шкаф для кухни',
          img: 'kitchens wardrobe.jpg',
          desc: 'Wonderful product',
          category:'tables',
          price:'59.99'
        },
        {
          id: '4',
          title: 'Диван серый',
          img: 'grey sofa.jpg',
          desc: 'Wonderful product',
          category:'sofas',
          price:'499.99'
        }
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
  }
  render() {
  return (
    <div className='wrapper'>
      <Header orders={this.state.orders}/>
      <Items items = {this.state.items} toAdd = {this.addToOrder}/>
      <Footer/>


    </div>
  )
  }
  addToOrder(item){
    let isInArray = false
    this.state.orders.forEach(el => {
      if (el.id === item.id)
      isInArray = true
    })
      if(!isInArray)
    this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
