import React, { useState } from 'react'
import Footer from './Components/Footer';
import Header from './Components/Header';
import Items from './Components/Items';
import Categories from './Components/Categories';
import FullItem from './Components/FullItem';



const App = () => {
  let [state, setState] = useState({
    orders: [],
    fullItem: false,
    currentItems: [],
    fullItems : [],
    items: [
      {
        id: '1',
        title: 'Стул серый',
        img: 'chair-grey.jpg',
        desc: 'Wonderful product',
        category:'chairs',
        price:'49.99'
      },
      {
        id: '2',
        title: 'Кресло серое',
        img: 'armchair-grey.jpg',
        desc: 'Wonderful product',
        category:'chairs',
        price:'69.99'
      },
      {
        id: '3',
        title: 'Шкаф для кухни',
        img: 'kitchens wardrobe.jpg',
        desc: 'Wonderful product',
        category:'tables',
        price:'59.99'
      },
      {
        id: '4',
        title: 'Диван серый',
        img: 'grey sofa.jpg',
        desc: 'Wonderful product',
        category:'sofas',
        price:'499.99'
      }
    ]
  })
  state.currentItems = state.items

  const toShowItem = (item) => {
    setState({fullItems: item})
    setState({fullItem: !state.fullItem})
  }

  const chooseCategory =(category) => {
    if (category === 'all'){
      setState({currentItems: state.items})
      return
    }

    setState({
      currentItems: state.items.filter(el => el.category === category)
    })
  }

  const deleteOrder = (id) => {
    setState({orders: state.orders.filter(el => el.id !== id)})
  }
  const addToOrder = (item) => {
    let isInArray = false
    state.orders.forEach(el => {
      if (el.id === item.id)
      isInArray = true
    })
      if(!isInArray)
    setState({orders: [...state.orders, item]})
  }
  return (
    <div className='wrapper'>
      <Header orders={state.orders} toDelete = {deleteOrder}/>
      <Categories chooseCategory = {chooseCategory}/>
      <Items items = {state.currentItems} toAdd = {addToOrder} toShow = {toShowItem}/>
      {state.fullItem && <FullItem toAdd = {addToOrder} toShow = {toShowItem} item = {state.fullItems}/>}
      <Footer/>
    </div>
  )

}

export default App
