import React, { useState } from 'react'
import { FaShoppingCart } from "react-icons/fa";
import Order from './Order';

const showOrders = (props) => {
  return (<div>
    {props.orders.map(el => (
    <Order key={el.id} item={el}/>
  ))}
  </div>)
}

const showNothing = () => {
  return(<div className='empty_const_header'></div>)
}

const Header = (props) => {
  let [cartOpen, setCartOpen] = useState(false)
  return (
    <header>
        <div>
            <span className='logo_header_header'>House Staff</span>
            <ul className='nav_header_header'>
              <li>Про нас</li>
              <li>Контакты</li>
              <li>Кабинет</li>
            </ul>
            <FaShoppingCart onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`shopcart_button_header ${cartOpen && 'active'}`}/>
            
            {cartOpen && (
              <div className='cartopen_div_header'>
                {props.orders.length > 0 ? 
                  showOrders(props) : showNothing()}
              </div>
            )}
        </div>
        <div className='presentation_header_header'></div>
        
    </header>
  )
}

export default Header
