  let [state, setState] = useState({
    orders: [],
    fullItem: false,
    currentItems: [],
    fullItems : [],
    items: [
      {
        id: '1',
        title: 'Стул серый',
        img: 'chair-grey.jpg',
        desc: 'Wonderful product',
        category:'chairs',
        price:'49.99'
