        title: 'Шкаф для кухни',
        img: 'kitchens wardrobe.jpg',
        desc: 'Wonderful product',
        category:'tables',
        price:'59.99'
      },
      {
        id: '4',
        title: 'Диван серый',
        img: 'grey sofa.jpg',
        desc: 'Wonderful product',
        category:'sofas',
        price:'499.99'
      }
    ]
  })
  state.currentItems = state.items

  const toShowItem = (item) => {
    setState((prev) => {
      let state = prev
      state.fullItems = item
      return state
      })
    setState((prevstate) => ({...prevstate, fullItem: !state.fullItem}))

  }



  const chooseCategory =(category) => {
    if (category === 'all'){
       setState((prev) => {
        let state = prev
        state.currentItems = state.items
        return state
