  }
  const addToOrder = (item) => {
    // let isInArray = false
    // state.orders.forEach(el => {
    //   if (el.id === item.id)
    //   isInArray = true
    // })
    //   if(!isInArray)
    // setState({orders: [...state.orders, item]})
  }
  return (
    <div className='wrapper'>
      <Header orders={state.orders} toDelete = {deleteOrder}/>
      <Categories chooseCategory = {chooseCategory}/>
      <Items items = {state.currentItems} toAdd = {addToOrder} toShow = {toShowItem}/>
      {state.fullItem && <FullItem toAdd = {addToOrder} toShow = {toShowItem} item = {state.fullItems}/>}
      <Footer/>
    </div>
  )

}

export default App
